import { Portal } from "../Portal";
import { Backdrop, Content } from "./styles";

export const Modal = ({open, onClose, children}) => {

    return (
        <Portal>
            <Backdrop onClick = {onClose}>
                <Content>
                    {children}
                </Content>
            </Backdrop>
        </Portal>
    )
}
