import { useState } from 'react';
import PropTypes from 'prop-types';

import { Wrapper, Input, Row, Button, Comment } from './styles';

const Form = ( props ) => {

    const [form, setForm] = useState({
        value: '',
        comment: '',
        date: new Date().toISOString().substring(0, 10)
    })

    const onSubmit = (e) => {
        e.preventDefault();

        props.onChange(form);
            setForm({
                ...form,
                value: '',
                comment: ''
            })
    }

    const onChange = (e) => {
        const { value, name } = e.target;

        setForm({
            ...form,
            [name]: value
        })
    }

        return (
            <Wrapper>
                <form onSubmit={onSubmit}>
                    <Row>
                        <Input name="value" 
                            type="number" 
                            placeholder="Money"
                            value={form.value}
                            onChange={onChange}/>

                        <Input type="date" 
                            name="date" 
                            value={form.date}
                            onChange={onChange}/>
                    </Row>

                    <Row>
                        <Button>Save</Button>
                        <Comment name="comment"
                            value={form.comment}
                            onChange={onChange}/>
                    </Row>
                </form>
            </Wrapper>
        )
}

Form.propTypes = {
    onChange: PropTypes.func
};

export default Form;
