import styled from "styled-components"

export const Backdrop = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(51,51,51,0.3);
    background-filter: blur(1px);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 1;
`
